mapper.select:
SELECT
(
    ( SELECT COUNT(*)
      FROM tb_order
      WHERE customer_id = #{customerId}
         AND create_date = CURRENT_DATE()
         AND status < 3
    ) >= 10
    OR
    ( SELECT COUNT(*)
      FROM tb_order
      WHERE customer_id = #{customerId}
         AND refund_date = CURRENT_DATE()
         AND status = 4
    ) >= 5
) AS illegal
